java // src/main/CountYourWords.java public class CountYourWords { public static Pair<Integer, HashMap<String, Integer>> count(ArrayList<String> fileLines) { HashMap<String, Integer> wordCounts = new HashMap<>(); for (String line : fileLines) { String[] words = line.toLowerCase().split("\\W+"); for (String word : words) { if (!word.isEmpty()) { wordCounts.put(word, wordCounts.getOrDefault(word, 0) + 1); } } } return new Pair<>(wordCounts.size(), wordCounts); } }

